/// Modified Bessel of the second kind order 1
///
/// Max ULP 0.5
pub fn f_k1(x: f64) -> f64 {
    if x < 0. {
        return f64::NAN;
    }

    if !x.is_normal() {
        if x == 0. {
            return f64::INFINITY;
        }
        if x.is_infinite() {
            return if x.is_sign_positive() { 0. } else { f64::NAN };
        }
        if x.is_nan() {
            return x + x;
        }
    }

    let xb = x.to_bits();

    if xb >= 0x4086140538aa7d38u64 {
        // 706.5025494880165
        return 0.;
    }

    if xb <= 0x3ff0000000000000 {
        return k1_small(x);
    }

    k1_asympt(x)
}

