    let p_den = DoubleDouble::mul_add(x8, f2, g0);

    let z = DoubleDouble::div(p_num, p_den);

    let mut r_e = DoubleDouble::quick_mult(e, r_sqrt);
    r_e = DoubleDouble::from_exact_add(r_e.hi, r_e.lo);
    r_e = DoubleDouble::quick_mult(r_e, e);
